import axios from "axios"
import { useState, useEffect } from "react";



function App() {

  const [info, setInfo] = useState({})
  const [ location, setLocation] = useState()
  const [listAdded, setListAdded] = useState([])

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=dbfc58248986c6dec618446e83e49191&units=metric`

 const search = (e) => {
  if(e.key === 'Enter'){
      axios.get(url).then((resp) => {
      setInfo(resp.data)
    }).catch((err)=> {console.log(err)})
    setLocation('')
  }
 }
 function searchList(location) {
  axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=dbfc58248986c6dec618446e83e49191&units=metric`).then((resp) => {
    setInfo(resp.data)
  }).catch((err)=> {console.log(err)})
    window.alert('Write the location correctly please! ')
  setLocation('')
 }

 function addList(name) {
    setListAdded([...listAdded, name])
    console.log(listAdded)
 }

  return (
    <div className="app"> 
      <div className="container">
        <div className="menu">
          <div><p>X</p></div>

          {listAdded.map((el,i) => {
            return <div key={i} onClick={() => searchList(el)}><span>{el}</span></div>
          })}
        </div>
        <div className="search">
          <input type="text"
          value={location || ''} 
          placeholder="Enter Location..."
          onKeyDown={search}
          onChange={(e)=> setLocation(e.target.value)}
          />
        </div>

          <div className="top">
              <div className="location">
                <p>{info.name}</p>
              </div>
              <div className="temp">
               {info.main 
               ? <div> 
                <h1>{info.main.temp.toFixed()}˚C</h1> <br></br>
                <button className="search" onClick={() => addList(info.name)}>+ ADD</button>
                </div> : null}

              </div>
              <div className="description">
                {info.main ? <p>{info?.weather[0].main}</p> : null}
              </div>
          </div>
            <div className="bottom">
            <div className="feels">
              {info.main ? <p className="bold">{info?.main.feels_like.toFixed()}˚C</p> : null}
              <p>Feels Like</p>
            </div>
            <div className="humidity">
              {info.main ? <p className="bold">{info?.main.humidity}%</p> : null}
              <p>Humidity</p>
            </div>
            <div className="wind">
              {info.main ? <p className="bold">{info?.wind.speed}m/s</p> : null}
              <p>Wind Speed</p>
            </div>
          </div>
      </div>
    </div>
  );
}

export default App;
