import axios from "axios"
import { useState, useEffect } from "react";



function App() {

  const [info, setInfo] = useState({})
  const [ location, setLocation] = useState()
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=dbfc58248986c6dec618446e83e49191`

 const search = (e) => {
  if(e.key === 'Enter'){
      axios.get(url).then((resp) => {
      setInfo(resp.data)
      console.log(resp.data)

    })
    setLocation('')
  }

 }

  return (
    <div className="app">
      <div className="search">
        <input type="text"
         value={location} 
         placeholder="Enter Location..."
         onKeyDown={search}
         onChange={(e)=> setLocation(e.target.value)}/>

      </div>
      <div className="container">
        <div className="top">
            <div className="location">
              <p>{info.name}</p>
            </div>
            <div className="temp">
             {info.main ? <h1>{info.main.temp}</h1> : null}
            </div> 
            <div className="description">
              <p>{info?.weather[0].main}</p>
            </div>
        </div>
        <div className="bottom">
          <div className="feels"> 
            <p className="bold">{info?.main.feels_like}</p>
            <p>Feels Like</p>
          </div> 
          <div className="humidity">
            <p className="bold">{info?.main.humidity}%</p>
            <p>Humidity</p>
          </div>
          <div className="wind">
            <p className="bold">{info?.wind.speed}MPH</p>
            <p>Wind Speed</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
